import React, { useState } from 'react';
import { PieChart, Pie, Cell, Tooltip, Legend } from 'recharts';
import { CalendarDays, Clock, Trophy, BookOpen, Gamepad2, FlaskConical } from 'lucide-react';

const RiwayatAktivitas = () => {
  const [activities, setActivities] = useState([
    { name: "Pembelajaran", value: 40 },
    { name: "Permainan", value: 30 },
    { name: "Eksperimen", value: 30 },
  ]);

  const [recentActivities, setRecentActivities] = useState([
    { 
      title: "Modul Fotosintesis", 
      date: "2024-11-01", 
      status: "Selesai",
      type: "Pembelajaran",
      duration: "45 menit",
      score: 85
    },
    { 
      title: "Permainan Tumbuhan", 
      date: "2024-11-02", 
      status: "Selesai",
      type: "Permainan",
      duration: "30 menit",
      score: 90
    },
    { 
      title: "Eksperimen Sel Hewan", 
      date: "2024-11-03", 
      status: "Sedang Berlangsung",
      type: "Eksperimen",
      duration: "60 menit",
      score: null
    },
  ]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'Selesai':
        return 'bg-green-500';
      case 'Sedang Berlangsung':
        return 'bg-blue-500';
      default:
        return 'bg-gray-500';
    }
  };

  const getActivityIcon = (type) => {
    switch (type) {
      case 'Pembelajaran':
        return <BookOpen size={20} className="text-blue-500" />;
      case 'Permainan':
        return <Gamepad2 size={20} className="text-green-500" />;
      case 'Eksperimen':
        return <FlaskConical size={20} className="text-purple-500" />;
      default:
        return null;
    }
  };

  const COLORS = ['#4CAF50', '#FF9800', '#03A9F4'];

  return (
    <div className="p-8 bg-gradient-to-b from-gray-50 to-gray-100 min-h-screen">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">Riwayat Aktivitas BioFun</h1>
        <p className="text-gray-600 mt-2">Rangkuman kegiatan pembelajaran biologi Anda</p>
      </div>

      {/* Main Content */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Chart Section */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <h2 className="text-xl font-semibold mb-4 text-center">Distribusi Aktivitas Belajar</h2>
          <div className="flex justify-center">
            <PieChart width={300} height={300}>
              <Pie
                data={activities}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={100}
                fill="#8884d8"
                label
              >
                {activities.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </div>
        </div>

        {/* Statistics Section */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <h2 className="text-xl font-semibold mb-4">Statistik Pembelajaran</h2>
          <div className="space-y-4">
            <div className="flex justify-between items-center p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition">
              <div className="flex items-center gap-3">
                <Clock size={20} className="text-blue-500" />
                <span className="font-medium">Total Waktu Belajar</span>
              </div>
              <span className="font-semibold">135 menit</span>
            </div>
            <div className="flex justify-between items-center p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition">
              <div className="flex items-center gap-3">
                <Trophy size={20} className="text-yellow-500" />
                <span className="font-medium">Rata-rata Nilai</span>
              </div>
              <span className="font-semibold">87.5</span>
            </div>
          </div>
        </div>
      </div>

      {/* Recent Activities Section */}
      <div className="mt-8 bg-white p-6 rounded-xl shadow-md">
        <h2 className="text-xl font-semibold mb-4">Aktivitas Terbaru</h2>
        <div className="space-y-4">
          {recentActivities.map((activity, index) => (
            <div 
              key={index} 
              className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition duration-200"
            >
              <div className="flex items-center gap-4">
                {getActivityIcon(activity.type)}
                <div>
                  <h3 className="font-semibold text-gray-800">{activity.title}</h3>
                  <div className="flex items-center gap-2 text-sm text-gray-500 mt-1">
                    <CalendarDays size={16} />
                    <span>{activity.date}</span>
                    <Clock size={16} className="ml-2" />
                    <span>{activity.duration}</span>
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-4">
                {activity.score && (
                  <div className="px-3 py-1 bg-yellow-100 text-yellow-800 rounded-full font-medium">
                    Nilai: {activity.score}
                  </div>
                )}
                <span className={`px-3 py-1 text-white rounded-full ${getStatusColor(activity.status)}`}>
                  {activity.status}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RiwayatAktivitas;
