import React, { useEffect, useState } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
import { Award, Book, Brain, Calendar, Fish, Flower, Star, Trophy } from 'lucide-react';

const Dashboard = () => {
  const [user, setUser] = useState({
    name: "Andi",
    grade: "Kelas 5",
    totalPoints: 750,
    streak: 5
  });

  const [learningStats, setLearningStats] = useState({
    hewan: { progress: 65, lastActivity: "Quiz Klasifikasi Hewan" },
    tumbuhan: { progress: 40, lastActivity: "Game Fotosintesis" },
    manusia: { progress: 85, lastActivity: "Video Sistem Pencernaan" }
  });

  const [achievements, setAchievements] = useState([
    "Ahli Biologi Muda",
    "Penjelajah Hewan",
    "Master Anatomi"
  ]);

  const weeklyActivity = [
    { name: 'Sen', menit: 45 },
    { name: 'Sel', menit: 30 },
    { name: 'Rab', menit: 60 },
    { name: 'Kam', menit: 25 },
    { name: 'Jum', menit: 45 },
    { name: 'Sab', menit: 50 },
    { name: 'Min', menit: 20 }
  ];

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-50 to-blue-50">
      {/* Header Section */}
      <div className="bg-gradient-to-r from-purple-600 to-pink-600 py-16" data-aos="fade-down">
        <div className="container mx-auto px-4">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-4xl md:text-5xl font-bold text-white mb-2">Dashboard BioFun</h1>
              <p className="text-xl text-white/90">Selamat Datang, {user.name}!</p>
              <p className="text-white/80">{user.grade}</p>
            </div>
            <div className="text-center">
              <div className="bg-white/20 backdrop-blur rounded-lg p-4">
                <p className="text-2xl font-bold text-white">{user.streak} Hari</p>
                <p className="text-sm text-white/90">Streak Belajar</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container mx-auto px-4 -mt-10">
        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-shadow" data-aos="zoom-in">
            <div className="flex items-center gap-3">
              <Fish className="text-purple-600" size={24} />
              <h3 className="text-xl font-bold text-purple-600">Hewan</h3>
            </div>
            <div className="mt-4">
              <div className="w-full bg-purple-100 rounded-full h-2.5">
                <div 
                  className="bg-purple-600 h-2.5 rounded-full" 
                  style={{ width: `${learningStats.hewan.progress}%` }}
                ></div>
              </div>
              <p className="text-sm text-gray-600 mt-2">
                Aktivitas terakhir: {learningStats.hewan.lastActivity}
              </p>
            </div>
          </div>

          {/* Repeat similar structure with data-aos for Tumbuhan and Manusia sections */}

        </div>

        {/* Activity Chart & Achievements */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <div className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-shadow" data-aos="fade-right">
            <div className="flex items-center gap-3 mb-4">
              <Calendar className="text-purple-600" size={24} />
              <h3 className="text-xl font-bold text-purple-600">Aktivitas Minggu Ini</h3>
            </div>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={weeklyActivity}>
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="menit" fill="#9333ea" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          <div className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-shadow" data-aos="fade-left">
            <div className="flex items-center gap-3 mb-4">
              <Trophy className="text-purple-600" size={24} />
              <h3 className="text-xl font-bold text-purple-600">Pencapaian</h3>
            </div>
            <div className="space-y-4">
              {achievements.map((achievement, index) => (
                <div key={index} className="flex items-center gap-3 p-3 bg-gradient-to-r from-purple-50 to-pink-50 rounded-lg">
                  <Award className="text-purple-600" size={20} />
                  <span className="text-gray-700">{achievement}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
