import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const GamePuzzle = () => {
  const [pieces, setPieces] = useState([
    {
      id: 1,
      image: 'https://i.imgur.com/wAhyELW.png',
      correctPosition: 1,
      position: 1
    },
    {
      id: 2,
      image: 'https://i.imgur.com/QT6wQsz.png',
      correctPosition: 2,
      position: 2
    },
    {
      id: 3,
      image: 'https://i.imgur.com/EPCCqAv.png',
      correctPosition: 3,
      position: 3
    },
    {
      id: 4,
      image: 'https://i.imgur.com/cqBLfK1.png',
      correctPosition: 4,
      position: 4
    }
  ]);
  const [isGameOver, setIsGameOver] = useState(false);

  const shuffleArray = (arr) => {
    const shuffled = [...arr];
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  };

  const handlePieceClick = (id) => {
    if (isGameOver) return;
    const updatedPieces = pieces.map((piece) => {
      if (piece.id === id) {
        piece.position = (piece.position % 4) + 1;
      }
      return piece;
    });
    setPieces(updatedPieces);
    const correctPositions = updatedPieces.every((piece) => piece.position === piece.correctPosition);
    if (correctPositions) {
      setIsGameOver(true);
      toast.success('Selamat! Kamu berhasil menyusun metamorfosis katak dengan benar!', {
        position: "top-center",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored"
      });
    }
  };

  const handleRestart = () => {
    setPieces(shuffleArray(pieces));
    setIsGameOver(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-100 to-blue-100 p-6 flex flex-col justify-center items-center">
      <div className="text-center mb-6">
        <h2 className="text-4xl font-bold text-purple-600 mb-4">Puzzle Metamorfosis Katak</h2>
        <p className="text-lg mb-6">Susun gambar-gambar ini menjadi urutan metamorfosis katak yang benar!</p>
        {isGameOver && (
          <button
            onClick={handleRestart}
            className="bg-blue-600 text-white px-6 py-3 rounded-lg transition-colors hover:bg-blue-700"
          >
            Main Lagi
          </button>
        )}
      </div>
      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-6 justify-center">
        {pieces.map((piece) => (
          <div
            key={piece.id}
            onClick={() => handlePieceClick(piece.id)}
            className={`cursor-pointer w-full max-w-xs mx-auto bg-white rounded-lg shadow-md hover:shadow-lg transition-all transform hover:-translate-y-2 ${
              piece.position === piece.correctPosition
                ? 'border-4 border-green-500'
                : 'border-4 border-gray-300'
            }`}
          >
            <img
              src={piece.image}
              alt={`Puzzle Piece ${piece.id}`}
              className="w-full h-48 object-contain rounded-t-lg"
            />
            <div className="p-4 text-center">
              <p className="text-lg font-semibold">Posisi: {piece.position}</p>
            </div>
          </div>
        ))}
      </div>
      <ToastContainer />
    </div>
  );
};

export default GamePuzzle;
