// pages/BelajarTumbuhan.js
import React, { useEffect } from 'react';
import { Search } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css'; 

const BelajarTumbuhan = () => {
  const navigate = useNavigate();

  useEffect(() => {
    AOS.init({
      duration: 1000, 
      once: false, 
    });
  }, []);

  const handleCategoryClick = (categoryId) => {
    if (categoryId === 'flowering') {
      navigate('/tumbuhan-berbunga');
    }
    // Add other category navigations here
  };

  const categories = [
    { id: 'flowering', name: 'Tumbuhan Berbunga', icon: 'üå∏' },
    { id: 'non-flowering', name: 'Tumbuhan Tidak Berbunga', icon: 'üåø' },
    { id: 'trees', name: 'Pohon', icon: 'üå≥' },
    { id: 'shrubs', name: 'Semak', icon: 'üå≤' },
    { id: 'ferns', name: 'Paku', icon: 'üçÉ' },
    { id: 'succulents', name: 'Sukulen', icon: 'üåµ' },
    { id: 'medicinal', name: 'Tumbuhan Obat', icon: 'üå±' },
    { id: 'carnivorous', name: 'Tumbuhan Karnivora', icon: 'ü™¥' },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-50 to-blue-50">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-green-600 to-teal-600 text-white py-16">
        <div className="container mx-auto px-4">
          <h1 className="text-4xl md:text-5xl font-bold mb-4" data-aos="fade-right"> {/* AOS on main title */}
            Belajar Mengenal Tumbuhan üåø
          </h1>
          <p className="text-xl mb-8" data-aos="fade-left" data-aos-delay="200"> {/* AOS on subtitle */}
            Mari mengenal berbagai jenis tumbuhan dan perannya dalam ekosistem!
          </p>
          
          {/* Search Bar */}
          <div className="relative max-w-2xl" data-aos="fade-up" data-aos-delay="400"> {/* AOS on search bar */}
            <input
              type="text"
              placeholder="Cari materi pembelajaran..."
              className="w-full px-6 py-3 rounded-full text-gray-800 focus:outline-none focus:ring-2 focus:ring-green-300"
            />
            <Search className="absolute right-4 top-3 text-gray-400" />
          </div>
        </div>
      </div>

      {/* Category Filters */}
      <div className="container mx-auto px-4 py-8">
        <h2 className="text-2xl font-bold mb-4 text-green-600" data-aos="fade-right"> {/* AOS on categories title */}
          Kategori Tumbuhan
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {categories.map((category) => (
            <div 
              key={category.id}
              className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-shadow duration-200 cursor-pointer"
              data-aos="zoom-in"
              onClick={() => handleCategoryClick(category.id)}
            >
              <div className="text-4xl mb-2">{category.icon}</div>
              <h3 className="text-xl font-bold text-green-600">{category.name}</h3>
              <p className="text-gray-600">Klik untuk melihat informasi lebih lanjut tentang {category.name.toLowerCase()}.</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BelajarTumbuhan;
