import React from 'react';

// Data gambar untuk galeri
const galleryData = [
  {
    category: 'Hewan',
    images: [
      'https://example.com/image-hewan1.jpg',
      'https://example.com/image-hewan2.jpg',
      'https://example.com/image-hewan3.jpg',
    ],
  },
  {
    category: 'Tumbuhan',
    images: [
      'https://example.com/image-tumbuhan1.jpg',
      'https://example.com/image-tumbuhan2.jpg',
      'https://example.com/image-tumbuhan3.jpg',
    ],
  },
  {
    category: 'Manusia',
    images: [
      'https://example.com/image-manusia1.jpg',
      'https://example.com/image-manusia2.jpg',
      'https://example.com/image-manusia3.jpg',
    ],
  },
];

// Komponen Kartu Gambar
const ImageCard = ({ image }) => {
  return (
    <div className="p-2 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
      <img src={image} alt="Galeri" className="rounded-lg shadow-md hover:scale-105 transition-transform duration-300" />
    </div>
  );
};

// Komponen Galeri
const Gallery = () => {
  return (
    <div className="min-h-screen py-10 bg-gradient-to-b from-yellow-50 to-pink-100">
      <div className="container mx-auto">
        <h2 className="text-4xl font-bold text-center mb-8 bg-gradient-to-r from-purple-600 to-pink-500 bg-clip-text text-transparent">
          Galeri BioFun
        </h2>
        {/* Menampilkan kategori galeri */}
        {galleryData.map((section, index) => (
          <div key={index} className="mb-12">
            <h3 className="text-3xl font-semibold text-center mb-6 text-purple-600">{section.category}</h3>
            <div className="flex flex-wrap justify-center">
              {/* Menampilkan gambar berdasarkan kategori */}
              {section.images.map((image, imgIndex) => (
                <ImageCard key={imgIndex} image={image} />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Gallery;
