import React, { useState } from 'react';
import { useAlert } from 'react-alert'; // Import useAlert hook
import { Alert } from 'react-alert-template-basic'; // Import template

const GameQuiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const alert = useAlert(); // Hook untuk mengakses alert

  const questions = [
    {
      question: "Organ manakah yang berperan dalam peredaran darah?",
      options: [
        "Jantung",
        "Paru-paru",
        "Ginjal",
        "Hati"
      ],
      correctAnswer: 0
    },
    {
      question: "Apa fungsi utama hemoglobin dalam darah?",
      options: [
        "Mengangkut oksigen",
        "Mengangkut nutrisi",
        "Menghancurkan kuman",
        "Mengangkut karbon dioksida"
      ],
      correctAnswer: 0
    },
    {
      question: "Bagian manakah yang bertugas memompa darah ke seluruh tubuh?",
      options: [
        "Jantung",
        "Paru-paru",
        "Ginjal",
        "Hati"
      ],
      correctAnswer: 0
    },
    {
      question: "Berapa lama waktu yang dibutuhkan darah untuk beredar di seluruh tubuh?",
      options: [
        "30 detik",
        "1 menit",
        "1 jam",
        "1 hari"
      ],
      correctAnswer: 0
    },
    {
      question: "Apa nama sel darah yang bertanggung jawab atas pembekuan darah?",
      options: [
        "Trombosit",
        "Leukosit",
        "Eritrosit",
        "Limfosit"
      ],
      correctAnswer: 0
    }
  ];

  const handleAnswerClick = (index) => {
    if (index === questions[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }

    if (currentQuestion === questions.length - 1) {
      setShowResult(true);
    } else {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const handlePlayAgain = () => {
    setCurrentQuestion(0);
    setScore(0);
    setShowResult(false);
  };

  const handleExit = () => {
    alert.show("Apakah Anda yakin ingin keluar dari permainan?", {
      timeout: 3000,
      type: 'info',
      onClose: () => window.location.href = '/', // Anda bisa mengganti dengan aksi lain
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-50 to-blue-50 flex flex-col items-center justify-center">
      {!showResult ? (
        <div className="bg-white rounded-xl p-8 shadow-lg w-full max-w-md">
          <h2 className="text-2xl font-bold text-purple-600 mb-4">
            {questions[currentQuestion].question}
          </h2>
          <div className="space-y-3">
            {questions[currentQuestion].options.map((option, index) => (
              <button
                key={index}
                className="bg-gray-100 hover:bg-gray-200 transition-colors duration-200 rounded-full px-6 py-3 w-full text-left"
                onClick={() => handleAnswerClick(index)}
              >
                {option}
              </button>
            ))}
          </div>
        </div>
      ) : (
        <div className="bg-white rounded-xl p-8 shadow-lg w-full max-w-md">
          <h2 className="text-2xl font-bold text-purple-600 mb-4">Hasil Kuis</h2>
          <p className="text-gray-600 mb-6">
            Kamu menjawab benar {score} dari {questions.length} pertanyaan.
          </p>
          <div className="flex justify-center gap-4">
            <button
              className="flex items-center gap-2 bg-purple-600 text-white px-4 py-2 rounded-full hover:bg-purple-700 transition-colors"
              onClick={handlePlayAgain}
            >
              <span>Main Lagi</span>
            </button>
            <button
              className="flex items-center gap-2 bg-gray-200 text-gray-600 px-4 py-2 rounded-full hover:bg-gray-300 transition-colors"
              onClick={handleExit}
            >
              <span>Keluar</span>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default GameQuiz;
