import React from 'react';
import { ArrowLeft } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const VideoMamalia = () => {
  const navigate = useNavigate();

  const videos = [
    {
      id: 1,
      title: "Kehidupan Singa di Alam Liar",
      duration: "10:25",
      thumbnail: "/api/placeholder/400/225",
      views: "1.2k",
      uploadDate: "2 hari yang lalu"
    },
    {
      id: 2,
      title: "Gajah Afrika: Sang Raksasa Cerdas",
      duration: "8:45",
      thumbnail: "/api/placeholder/400/225",
      views: "985",
      uploadDate: "5 hari yang lalu"
    },
    {
      id: 3,
      title: "Lumba-lumba: Mamalia Laut yang Mengagumkan",
      duration: "12:30",
      thumbnail: "/api/placeholder/400/225",
      views: "1.5k",
      uploadDate: "1 minggu yang lalu"
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-50 to-blue-50">
      {/* Navigation Header */}
      <div className="bg-white shadow-md">
        <div className="container mx-auto px-4 py-4">
          <button 
            onClick={() => navigate(-1)}
            className="flex items-center text-purple-600 hover:text-purple-800"
          >
            <ArrowLeft className="w-5 h-5 mr-2" />
            Kembali
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-purple-600 mb-8">Video Pembelajaran Mamalia</h1>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {videos.map((video) => (
            <div key={video.id} className="bg-white rounded-xl shadow-lg overflow-hidden">
              <div className="relative">
                <img 
                  src={video.thumbnail} 
                  alt={video.title}
                  className="w-full h-48 object-cover"
                />
                <span className="absolute bottom-2 right-2 bg-black bg-opacity-75 text-white px-2 py-1 rounded text-sm">
                  {video.duration}
                </span>
              </div>
              <div className="p-4">
                <h3 className="font-bold text-lg mb-2">{video.title}</h3>
                <div className="flex justify-between text-sm text-gray-600">
                  <span>{video.views} tayangan</span>
                  <span>{video.uploadDate}</span>
                </div>
                <button 
                  className="mt-4 w-full bg-purple-600 text-white py-2 rounded-lg hover:bg-purple-700 transition-colors"
                  onClick={() => alert('Memulai video: ' + video.title)}
                >
                  Putar Video
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default VideoMamalia;
