import React from 'react';

// Data gambar untuk galeri
const galleryData = [
  {
    category: 'Hewan',
    images: [
      'https://source.unsplash.com/100x100/?animal',
      'https://source.unsplash.com/100x100/?cat',
      'https://source.unsplash.com/100x100/?dog',
      'https://source.unsplash.com/100x100/?lion',
      'https://source.unsplash.com/100x100/?elephant',
      'https://source.unsplash.com/100x100/?tiger',
    ],
  },
  {
    category: 'Tumbuhan',
    images: [
      'https://source.unsplash.com/100x100/?plant',
      'https://source.unsplash.com/100x100/?tree',
      'https://source.unsplash.com/100x100/?flower',
      'https://source.unsplash.com/100x100/?forest',
      'https://source.unsplash.com/100x100/?leaf',
      'https://source.unsplash.com/100x100/?grass',
    ],
  },
  {
    category: 'Manusia',
    images: [
      'https://source.unsplash.com/100x100/?people',
      'https://source.unsplash.com/100x100/?children',
      'https://source.unsplash.com/100x100/?human',
      'https://source.unsplash.com/100x100/?person',
      'https://source.unsplash.com/100x100/?face',
      'https://source.unsplash.com/100x100/?hands',
    ],
  },
];

// Komponen Kartu Gambar
const ImageCard = ({ image }) => {
  return (
    <div className="p-2 w-24 h-24">
      <img src={image} alt="Galeri" className="rounded-lg shadow-md hover:scale-105 transition-transform duration-300 object-cover w-full h-full" />
    </div>
  );
};

// Komponen Galeri
const Gallery = () => {
  return (
    <div className="min-h-screen py-10 bg-gradient-to-b from-yellow-50 to-pink-100">
      <div className="container mx-auto">
        {/* Menampilkan kategori galeri hewan */}
        <div className="mb-12">
          <h3 className="text-3xl font-semibold text-center mb-6 text-purple-600">Hewan</h3>
          <div className="flex flex-wrap justify-center">
            {galleryData[0].images.map((image, imgIndex) => (
              <ImageCard key={imgIndex} image={image} />
            ))}
          </div>
        </div>

        {/* Menampilkan kategori galeri tumbuhan */}
        <div className="mb-12">
          <h3 className="text-3xl font-semibold text-center mb-6 text-green-600">Tumbuhan</h3>
          <div className="flex flex-wrap justify-center">
            {galleryData[1].images.map((image, imgIndex) => (
              <ImageCard key={imgIndex} image={image} />
            ))}
          </div>
        </div>

        {/* Menampilkan kategori galeri manusia */}
        <div className="mb-12">
          <h3 className="text-3xl font-semibold text-center mb-6 text-blue-600">Manusia</h3>
          <div className="flex flex-wrap justify-center">
            {galleryData[2].images.map((image, imgIndex) => (
              <ImageCard key={imgIndex} image={image} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Gallery;
