import React from 'react';

// Komponen Header
const Header = () => {
  return (
    <header className="bg-gradient-to-r from-purple-600 to-pink-500 text-white py-8">
      <div className="container mx-auto text-center">
        <h1 className="text-5xl font-bold">Selamat Datang di BioFun</h1>
        <p className="mt-4 text-xl">Platform pembelajaran biologi yang interaktif dan menyenangkan untuk anak-anak!</p>
      </div>
    </header>
  );
};

// Komponen Tentang Kami
const AboutUs = () => {
  return (
    <div className="min-h-screen py-20 bg-gradient-to-b from-purple-50 to-pink-50">
      <div className="container mx-auto text-center">
        <h2 className="text-4xl font-bold mb-8 bg-gradient-to-r from-purple-600 to-pink-500 bg-clip-text text-transparent">
          Tentang Kami
        </h2>
        <p className="text-lg text-gray-700 px-4 md:px-20">
          BioFun adalah platform pembelajaran biologi yang dirancang untuk membantu anak-anak memahami konsep-konsep biologi
          secara interaktif dan menyenangkan. Kami percaya bahwa pembelajaran tidak harus membosankan. Dengan menggunakan teknologi
          dan metode pengajaran yang inovatif, kami ingin menciptakan lingkungan yang membuat belajar biologi menjadi pengalaman
          yang mengasyikkan.
          <br /><br />
          Kami terdiri dari tim pendidik, ahli biologi, dan pengembang teknologi yang berdedikasi untuk menciptakan konten yang
          relevan dan menarik. Misi kami adalah menginspirasi rasa ingin tahu alamiah anak-anak terhadap dunia di sekitar mereka
          dan membantu mereka membangun fondasi pengetahuan yang kuat di bidang sains.
          <br /><br />
          Di BioFun, kami juga berfokus pada pentingnya pembelajaran berbasis eksperimen, di mana anak-anak dapat langsung terlibat
          dalam kegiatan praktis yang menggambarkan konsep biologi secara nyata. Kami percaya bahwa dengan mendekatkan anak-anak
          pada cara-cara pembelajaran yang menarik, mereka dapat berkembang menjadi individu yang lebih kreatif dan inovatif.
        </p>
      </div>
    </div>
  );
};

// Komponen Footer
const Footer = () => {
  return (
    <footer className="bg-gray-800 text-white py-8">
      <div className="container mx-auto text-center">
        <p>&copy; 2024 BioFun. Semua Hak Cipta Dilindungi.</p>
      </div>
    </footer>
  );
};

// Komponen App Utama
const App = () => {
  return (
    <div>
      <Header />
      <AboutUs />
      <Footer />
    </div>
  );
};

export default App;
