import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const GameQuiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const questions = [
    {
      question: "Organ manakah yang berperan dalam peredaran darah?",
      options: ["Jantung", "Paru-paru", "Ginjal", "Hati"],
      correctAnswer: 0
    },
    {
      question: "Apa warna bendera Indonesia?",
      options: ["Merah Putih", "Kuning Hijau", "Biru Merah", "Hitam Putih"],
      correctAnswer: 0
    },
    {
      question: "Ibukota negara Indonesia adalah?",
      options: ["Jakarta", "Bandung", "Surabaya", "Semarang"],
      correctAnswer: 0
    },
    {
      question: "Apa ibu kota provinsi Jawa Barat?",
      options: ["Bandung", "Bogor", "Bekasi", "Depok"],
      correctAnswer: 0
    },
    {
      question: "Apa nama gunung tertinggi di Indonesia?",
      options: ["Bromo", "Rinjani", "Pangrango", "Semeru"],
      correctAnswer: 3
    },
    {
      question: "Apa nama pahlawan nasional yang memimpin perlawanan terhadap Belanda?",
      options: ["Soekarno", "Sudirman", "Diponegoro", "Kartini"],
      correctAnswer: 2
    },
    {
      question: "Apa nama tarian tradisional Indonesia yang berasal dari Bali?",
      options: ["Tari Saman", "Tari Pendet", "Tari Jaipong", "Tari Kecak"],
      correctAnswer: 1
    },
    {
      question: "Apa nama salah satu danau terbesar di Indonesia?",
      options: ["Danau Toba", "Danau Sentani", "Danau Maninjau", "Danau Tempe"],
      correctAnswer: 0
    },
    {
      question: "Apa bahasa resmi negara Indonesia?",
      options: ["Bahasa Jawa", "Bahasa Sunda", "Bahasa Indonesia", "Bahasa Betawi"],
      correctAnswer: 2
    },
    {
      question: "Apa nama salah satu candi terbesar di Indonesia?",
      options: ["Candi Borobudur", "Candi Prambanan", "Candi Gedong Songo", "Candi Muara Takus"],
      correctAnswer: 0
    }
  ];

  const handleAnswerClick = (index) => {
    if (index === questions[currentQuestion].correctAnswer) {
      setScore(score + 1);
      toast.success("Jawaban Benar!");
    } else {
      toast.error("Jawaban Salah!");
    }

    if (currentQuestion === questions.length - 1) {
      setShowResult(true);
    } else {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const handlePlayAgain = () => {
    setCurrentQuestion(0);
    setScore(0);
    setShowResult(false);
  };

  const handleExit = () => {
    toast.success("Anda keluar dari permainan!", {
      autoClose: 2000
    });
    setTimeout(() => window.location.href = '/', 2000);
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md max-w-xl mx-auto">
      {!showResult ? (
        <>
          <h2 className="text-2xl font-bold mb-4">
            {questions[currentQuestion].question}
          </h2>
          <div className="space-y-2">
            {questions[currentQuestion].options.map((option, index) => (
              <button
                key={index}
                className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-md w-full text-left"
                onClick={() => handleAnswerClick(index)}
              >
                {option}
              </button>
            ))}
          </div>
        </>
      ) : (
        <>
          <h2 className="text-2xl font-bold mb-4">Hasil Kuis</h2>
          <p className="text-lg mb-4">
            Kamu menjawab benar {score} dari {questions.length} pertanyaan.
          </p>
          <div className="flex justify-between">
            <button
              className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md"
              onClick={handlePlayAgain}
            >
              Main Lagi
            </button>
            <button
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md"
              onClick={handleExit}
            >
              Keluar
            </button>
          </div>
        </>
      )}
      <ToastContainer />
    </div>
  );
};

export default GameQuiz;
